
public class Hello {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("Hello World !!");
		System.out.printf("%n" + "Hello");
		//Note the second statement uses 'printf' to use the line separator appropriate to your platform
		//in comparison to the \n which just introduces newline characters
		//println introduces line breaks using the platform's preferred line separator
		
	}

}
